	//定义全局变量
	var Cuser = false;
	var Cpass = false;
	var Crepass = false;
	var Cemail = true;
	var Cphone = true;
	//绑定表单事件
	$('form').submit(function(){
		//触发丧失焦点事件
		$('input').trigger('blur');

		//检测是否能够提交
		if(Cuser && Cpass && Crepass && Cemail && Cphone){
			//提交
			return true;
		}
		return false;
	})

	//绑定获取焦点事件  显示提示信息
	$('input[name=username]').focus(function(){
		//获取文本
		var text = $(this).attr('readme');
		//设置文本
		$('.success1').html(text).css('color','green');
	})
	$('input[name=password]').focus(function(){
		//获取文本
		var text = $(this).attr('readme');
		//设置文本
		$('.success2').html(text).css('color','green');
	})
	$('input[name=repassword]').focus(function(){
		//获取文本
		var text = $(this).attr('readme');
		//设置文本
		$('.success3').html(text).css('color','green');
	})
	$('input[name=yzm]').focus(function(){
		//获取文本
		var text = $(this).attr('readme');
		//设置文本
		$('.success4').html(text).css('color','green');
	})


	//绑定丧失焦点事件
	$('input[name=username]').blur(function(){
		//正则
		var reg = /^\w{6,16}$/;
		//获取用户名
		var username = $(this).val();
		//检测
		var res = reg.test(username);
		if(!res){
			// alert('格式不正确');
			$('.success1').html('用户名格式不正确').css('color','red');
			Cuser = false;
			//停止
			return false;
		}
		var inp = $(this);
		//发送ajax验证用户名是否可用
		$.ajax({
			url:"yonghu",
			data:{username:username},
			type:'get',
			// 同步
			async:false,
			success:function(data){
				//如果用户名可用
				if(data == 0){
					// alert('可用');
					$('.success1').html('√').css('color','blue');
					Cuser = true;
				}else{
					$('.success1').html('用户名已存在').css('color','red');
					Cuser = false;
				}
			}
		})
	})
	//密码
	$('input[name=password]').blur(function(){
		//获取密码
		var pass = $(this).val();
		//声明正则
		var reg = /^\w{6,16}$/;
		//验证
		if(reg.test(pass)){
			//正确
			$('.success2').html('√').css('color','blue');
			Cpass = true;
		}else{
			//错误
			$('.success2').html('密码格式不正确').css('color','red');
			Cpass = false;
		}
	})

	//q确认密码
	$('input[name=repassword]').blur(function(){
		//获取确认密码
		var repass = $(this).val();
		//获取密码
		var pass = $('input[name=password]').val();
		//验证
		if(pass == repass && repass != ''){
			//正确
			$('.success3').html('√').css('color','blue');
			$(this).removeClass();
			Crepass = true;

		}else{
			//错误
			$('.success3').html('两次密码不一样').css('color','red');
			Crepass = false;

		}
	})
